import React, { useState } from 'react'

const foodCarbonData = [
  {
    name: 'Beef',
    co2: 99.5,
    unit: 'kg CO₂e/kg',
    image: 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=400&q=80',
    description:
      'Beef has the highest carbon footprint among common foods, mainly due to methane emissions from cattle digestion, land use, and feed production.',
    calculation:
      'The carbon content for beef is calculated by considering greenhouse gases released from feed production, enteric fermentation (methane from digestion), manure management, and transportation. The value represents the average emissions per kilogram of beef produced.',
  },
  {
    name: 'Lamb & Mutton',
    co2: 39.7,
    unit: 'kg CO₂e/kg',
    image: 'data:image/jpeg;base64,',
    description:
      'Lamb and mutton have a high carbon footprint, largely due to methane emissions and the land required for grazing.',
    calculation:
      'The carbon content for lamb & mutton is calculated by summing up emissions from animal digestion (methane), feed production, manure, and land use changes. The value is an average per kilogram of meat.',
  },
  {
    name: 'Cheese',
    co2: 23.9,
    unit: 'kg CO₂e/kg',
    image: 'data:image/jpeg;base64,',
    description:
      'Cheese production requires large amounts of milk, which increases its carbon footprint due to dairy farming emissions.',
    calculation:
      'Cheese carbon content is calculated by accounting for milk production (including feed, methane, and manure), processing, and transportation. The value is per kilogram of cheese produced.',
  },
  {
    name: 'Chicken',
    co2: 9.9,
    unit: 'kg CO₂e/kg',
    image: 'data:image/jpeg;base64,',
    description:
      'Chicken has a lower carbon footprint than red meats, as chickens convert feed into meat more efficiently and emit less methane.',
    calculation:
      'Chicken’s carbon footprint includes emissions from feed production, manure management, and energy used in farming and processing. The value is an average per kilogram of chicken meat.',
  },
  {
    name: 'Eggs',
    co2: 4.7,
    unit: 'kg CO₂e/kg',
    image: 'data:image/jpeg;base64,',
    description:
      'Eggs have a moderate carbon footprint, mainly from feed production and manure management in poultry farming.',
    calculation:
      'Egg carbon content is calculated by considering emissions from feed, housing, manure, and processing. The value represents average emissions per kilogram of eggs.',
  },
  {
    name: 'Tofu',
    co2: 3.2,
    unit: 'kg CO₂e/kg',
    image: 'data:image/jpeg;base64,',
    description:
      'Tofu, made from soybeans, has a low carbon footprint compared to animal products, as plants require less land and emit fewer greenhouse gases.',
    calculation:
      'Tofu’s carbon content includes emissions from soybean cultivation, processing, and transportation. The value is per kilogram of tofu produced.',
  },
  {
    name: 'Nuts',
    co2: 0.4,
    unit: 'kg CO₂e/kg',
    image: 'data:image/jpeg;base64,',
    description:
      'Nuts have the lowest carbon footprint, as trees absorb CO₂ during growth and require minimal processing.',
    calculation:
      'Nut carbon content is calculated by considering emissions from cultivation, processing, and transportation, offset by carbon sequestration by trees.',
  },
]

const Home = () => {
  const [selectedFood, setSelectedFood] = useState(null)

  const handleBack = () => setSelectedFood(null)

  const food = foodCarbonData.find((item) => item.name === selectedFood)

  return (
    <section style={{ padding: '2rem', fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif", maxWidth: '900px', margin: '0 auto' }}>
      <h2 style={{ textAlign: 'center', color: '#2e7d32', marginBottom: '0.5rem' }}>
        Carbon Footprint of Common Foods
      </h2>
      <p style={{ textAlign: 'center', color: '#4caf50', marginBottom: '2rem', fontSize: '1.1rem' }}>
        Every food item has a carbon footprint – the greenhouse gases emitted during its production, transportation, and preparation. Understanding these numbers helps us make better choices for the planet.
      </p>

      {!selectedFood ? (
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(260px, 1fr))',
            gap: '1.5rem',
          }}
        >
          {foodCarbonData.map(({ name, co2, unit, image }) => (
            <div
              key={name}
              onClick={() => setSelectedFood(name)}
              role="button"
              tabIndex={0}
              onKeyDown={(e) => {
                if (e.key === 'Enter' || e.key === ' ') setSelectedFood(name)
              }}
              style={{
                cursor: 'pointer',
                borderRadius: '12px',
                boxShadow: '0 4px 15px rgba(76, 175, 80, 0.2)',
                backgroundColor: '#e8f5e9',
                padding: '1rem',
                transition: 'transform 0.3s ease, box-shadow 0.3s ease',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                textAlign: 'center',
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'scale(1.05)'
                e.currentTarget.style.boxShadow = '0 8px 30px rgba(76, 175, 80, 0.4)'
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'scale(1)'
                e.currentTarget.style.boxShadow = '0 4px 15px rgba(76, 175, 80, 0.2)'
              }}
            >
              <img
                src={image}
                alt={name}
                loading="lazy"
                style={{ width: '100%', height: '160px', objectFit: 'cover', borderRadius: '8px', marginBottom: '1rem' }}
              />
              <h3 style={{ margin: '0 0 0.5rem', color: '#2e7d32' }}>{name}</h3>
              <p style={{ margin: 0, fontWeight: '600', color: '#388e3c' }}>
                {co2} {unit}
              </p>
            </div>
          ))}
        </div>
      ) : (
        <article
          style={{
            marginTop: '2rem',
            backgroundColor: '#f0f9f4',
            borderRadius: '12px',
            padding: '2rem',
            boxShadow: '0 6px 25px rgba(76, 175, 80, 0.3)',
          }}
        >
          <button
            onClick={handleBack}
            aria-label="Go back to food list"
            style={{
              backgroundColor: '#4caf50',
              color: '#fff',
              border: 'none',
              borderRadius: '6px',
              padding: '0.5rem 1rem',
              cursor: 'pointer',
              fontWeight: '600',
              marginBottom: '1.5rem',
              transition: 'background-color 0.3s ease',
            }}
            onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = '#388e3c')}
            onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = '#4caf50')}
          >
            ← Back
          </button>
          <div style={{ display: 'flex', gap: '2rem', flexWrap: 'wrap' }}>
            <img
              src={food.image}
              alt={food.name}
              style={{ flex: '1 1 250px', borderRadius: '12px', objectFit: 'cover', maxHeight: '300px', boxShadow: '0 4px 15px rgba(0,0,0,0.1)' }}
              loading="lazy"
            />
            <div style={{ flex: '2 1 400px' }}>
              <h3 style={{ color: '#2e7d32', marginTop: 0 }}>{food.name}</h3>
              <p style={{ fontWeight: '700', color: '#388e3c', fontSize: '1.2rem', margin: '0.5rem 0' }}>
                {food.co2} {food.unit}
              </p>
              <p style={{ lineHeight: '1.6', marginBottom: '1rem' }}>{food.description}</p>
              <details style={{ fontSize: '0.95rem', color: '#2e7d32' }}>
                <summary style={{ cursor: 'pointer', fontWeight: '600', marginBottom: '0.5rem' }}>
                  How is this calculated?
                </summary>
                <p style={{ marginTop: '0.5rem', lineHeight: '1.5' }}>{food.calculation}</p>
              </details>
            </div>
          </div>
        </article>
      )}
    </section>
  )
}

export default Home
